function checkRegex(){const e=regexInput.value,t=textInput.value;try{const n=new RegExp(e,"g"),c=t.match(n)||[];c.length>0?(resultDiv.innerHTML=`<strong>Coincidencias encontradas:</strong> ${c.join(", ")}`,resultDiv.style.color="#4caf50"):(resultDiv.innerHTML="No se encontraron coincidencias.",resultDiv.style.color="#ff0000")}catch(e){resultDiv.innerHTML="Error: Expresión regular no válida.",resultDiv.style.color="#ff0000"}}const regexInput=document.getElementById("regex-input"),textInput=document.getElementById("text-input"),resultDiv=document.getElementById("result"),checkButton=document.getElementById("check-button");checkButton.addEventListener("click",checkRegex);